        titleLabel.setBorder(BorderFactory.createEmptyBorder(0, 0, 10, 0));        titlePanel.add(titleLabel, BorderLayout.CENTER);        JLabel subtitleLabel = new JLabel("View and manage all hotel rooms");        subtitleLabel.setFont(new Font("Segoe UI", Font.PLAIN, 16));        subtitleLabel.setForeground(new Color(230, 230, 250));        subtitleLabel.setHorizontalAlignment(SwingConstants.CENTER);        titlePanel.add(subtitleLabel, BorderLayout.SOUTH);        contentPanel.add(titlePanel, BorderLayout.NORTH);        // Ù„ÙˆØ­Ø© Ø§Ù„Ø¬Ø¯ÙˆÙ„        JPanel tablePanel = new JPanel(new BorderLayout());        tablePanel.setOpaque(false);        tablePanel.setBorder(BorderFactory.createCompoundBorder(                BorderFactory.createLineBorder(new Color(255, 215, 0, 80), 2),                BorderFactory.createEmptyBorder(15, 15, 15, 15)        ));        // Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„Ø¬Ø¯ÙˆÙ„ Ù…Ø¹ ØªØµÙ…ÙŠÙ… Ù…Ø­Ø³Ù†        table = new JTable() {            @Override            public boolean isCellEditable(int row, int column) {                return false; // Ù…Ù†Ø¹ ØªØ¹Ø¯ÙŠÙ„ Ø§Ù„Ø®Ù„Ø§ÙŠØ§ Ù…Ø¨Ø§Ø´Ø±Ø©            }        };        // Ù‡Ø°Ø§ Ø§Ù„Ø³Ø·Ø± ÙŠØ­Ù„ Ù…Ø´ÙƒÙ„Ø© Ø§Ù„ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¶Ø¨Ø§Ø¨ÙŠ        table.setDoubleBuffered(true);        // ØªØ®ØµÙŠØµ Ø§Ù„Ù…Ø¸Ù‡Ø± Ø§Ù„Ø£Ø³Ø§Ø³ÙŠ Ù„Ù„Ø¬Ø¯ÙˆÙ„        table.setRowHeight(35);        table.setFont(new Font("Segoe UI", Font.PLAIN, 14));        table.setForeground(new Color(240, 240, 240));        table.setBackground(new Color(255, 255, 255, 20));        table.setGridColor(new Color(255, 215, 0, 40));        table.setSelectionBackground(new Color(70, 130, 180, 150));        table.setSelectionForeground(Color.WHITE);        table.setShowVerticalLines(true);        table.setShowHorizontalLines(true);        // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø£ÙˆÙ„Ø§Ù‹        boolean dataLoaded = false;        try {            Z_Con c = new Z_Con();            String RoomInfo = "SELECT * FROM room ORDER BY roomnumber";            ResultSet resultSet = c.statement.executeQuery(RoomInfo);            table.setModel(DbUtils.resultSetToTableModel(resultSet));            dataLoaded = true;        } catch (Exception e) {            e.printStackTrace();            JOptionPane.showMessageDialog(this,                    "Error loading room data: " + e.getMessage(),                    "Database Error",                    JOptionPane.ERROR_MESSAGE);        }        // ÙÙ‚Ø· Ø¥Ø°Ø§ ØªÙ… ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§ØªØŒ Ù‚Ù… Ø¨ØªØ®ØµÙŠØµ Ø§Ù„Ø£Ø¹Ù…Ø¯Ø©        if (dataLoaded && table.getColumnCount() > 0) {            // ØªØ®ØµÙŠØµ Ø±Ø£Ø³ Ø§Ù„Ø¬Ø¯ÙˆÙ„            JTableHeader header = table.getTableHeader();            header.setFont(new Font("Segoe UI", Font.BOLD, 14));            header.setBackground(new Color(30, 60, 90));            header.setForeground(new Color(255, 215, 0));            header.setPreferredSize(new Dimension(header.getWidth(), 40));            // ØªØ®ØµÙŠØµ Ø¹Ø±Ø¶ Ø§Ù„Ø£Ø¹Ù…Ø¯Ø© - ÙÙ‚Ø· Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ù…ÙˆØ¬ÙˆØ¯Ø©            if (table.getColumnCount() >= 1) {                table.getColumnModel().getColumn(0).setPreferredWidth(80);  // Room No            }            if (table.getColumnCount() >= 2) {                table.getColumnModel().getColumn(1).setPreferredWidth(100); // Availability            }            if (table.getColumnCount() >= 3) {                table.getColumnModel().getColumn(2).setPreferredWidth(120); // Clean Status            }            if (table.getColumnCount() >= 4) {                table.getColumnModel().getColumn(3).setPreferredWidth(80);  // Price            }            if (table.getColumnCount() >= 5) {                table.getColumnModel().getColumn(4).setPreferredWidth(100); // Bed Type            }            // ØªØ®ØµÙŠØµ Ø¹Ø±Ø¶ Ø§Ù„Ø®Ù„Ø§ÙŠØ§            DefaultTableCellRenderer centerRenderer = new DefaultTableCellRenderer();            centerRenderer.setHorizontalAlignment(JLabel.CENTER);            centerRenderer.setForeground(new Color(240, 240, 240));            centerRenderer.setBackground(new Color(255, 255, 255, 20));            // ØªØ·Ø¨ÙŠÙ‚ Ø§Ù„ØªØ®ØµÙŠØµ Ø¹Ù„Ù‰ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ø¹Ù…Ø¯Ø©            for (int i = 0; i < table.getColumnCount(); i++) {                table.getColumnModel().getColumn(i).setCellRenderer(centerRenderer);            }            // ØªØ®ØµÙŠØµ Ø®Ø§Øµ Ù„Ù„Ø£Ø¹Ù…Ø¯Ø© Ø§Ù„Ù…ØªØ¹Ù„Ù‚Ø© Ø¨Ø§Ù„Ø­Ø§Ù„Ø©            DefaultTableCellRenderer statusRenderer = new DefaultTableCellRenderer() {                @Override                public Component getTableCellRendererComponent(JTable table, Object value,                                                               boolean isSelected, boolean hasFocus, int row, int column) {                    Component c = super.getTableCellRendererComponent(table, value, isSelected, hasFocus, row, column);                    // ØªØ­Ø¯ÙŠØ¯ Ù„ÙˆÙ† Ø­Ø³Ø¨ Ø­Ø§Ù„Ø© Ø§Ù„ØºØ±ÙØ©                    if (column == 1 && value != null) { // Ø¹Ù…ÙˆØ¯ Availability                        String status = value.toString();                        if (status.equalsIgnoreCase("Available")) {                            c.setBackground(new Color(60, 179, 113, 100)); // Ø£Ø®Ø¶Ø±                            c.setForeground(new Color(200, 255, 200));                        } else if (status.equalsIgnoreCase("Occupied")) {                            c.setBackground(new Color(231, 76, 60, 100)); // Ø£Ø­Ù…Ø±                            c.setForeground(new Color(255, 200, 200));                        } else {                            c.setBackground(new Color(255, 193, 7, 100)); // Ø£ØµÙØ±                            c.setForeground(new Color(255, 255, 200));                        }                    } else if (column == 2 && value != null) { // Ø¹Ù…ÙˆØ¯ Clean Status                        String cleanStatus = value.toString();                        if (cleanStatus.equalsIgnoreCase("Clean")) {                            c.setBackground(new Color(46, 204, 113, 80)); // Ø£Ø®Ø¶Ø± ÙØ§ØªØ­                            c.setForeground(new Color(200, 255, 200));                        } else {                            c.setBackground(new Color(255, 193, 7, 80)); // Ø£ØµÙØ±                            c.setForeground(new Color(255, 255, 200));                        }                    } else {                        c.setBackground(new Color(255, 255, 255, 20));                        c.setForeground(new Color(240, 240, 240));                    }                    if (isSelected) {                        c.setBackground(new Color(70, 130, 180, 150));                        c.setForeground(Color.WHITE);                    }                    return c;                }            };            statusRenderer.setHorizontalAlignment(JLabel.CENTER);            if (table.getColumnCount() >= 2) {                table.getColumnModel().getColumn(1).setCellRenderer(statusRenderer);            }            if (table.getColumnCount() >= 3) {                table.getColumnModel().getColumn(2).setCellRenderer(statusRenderer);            }        }        // Ø¥Ø¶Ø§ÙØ© Ø§Ù„Ø¬Ø¯ÙˆÙ„ Ø¥Ù„Ù‰ ScrollPane        scrollPane = new JScrollPane(table);        scrollPane.setBorder(null);        scrollPane.getViewport().setBackground(new Color(255, 255, 255, 10));        scrollPane.setOpaque(false);        scrollPane.getViewport().setOpaque(false);        tablePanel.add(scrollPane, BorderLayout.CENTER);        // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ØºØ±Ù - ØªØ®Ø²ÙŠÙ†Ù‡Ø§ ÙÙŠ Ù…ØªØºÙŠØ± Ø§Ù„ÙƒÙ„Ø§Ø³ Ù„Ù„ÙˆØµÙˆÙ„ Ù„Ø§Ø­Ù‚Ø§Ù‹        statsPanel = new JPanel(new GridLayout(1, 3, 20, 0));        statsPanel.setOpaque(false);        statsPanel.setBorder(BorderFactory.createEmptyBorder(15, 0, 15, 0));        int totalRooms = dataLoaded ? table.getRowCount() : 0;        String availableCount = getAvailableCount();        String occupiedCount = getOccupiedCount();        String[][] statsData = {                {"ğŸ›ï¸", "Total Rooms", String.valueOf(totalRooms)},                {"âœ…", "Available", availableCount},                {"ğŸ”„", "Occupied", occupiedCount}        };        Color[] statsColors = {                new Color(70, 130, 180),   // Steel Blue                new Color(60, 179, 113),   // Medium Sea Green                new Color(231, 76, 60)     // Red        };        for (int i = 0; i < statsData.length; i++) {            statsPanel.add(createStatCard(statsData[i][0], statsData[i][1], statsData[i][2], statsColors[i]));        }        tablePanel.add(statsPanel, BorderLayout.SOUTH);        contentPanel.add(tablePanel, BorderLayout.CENTER);        // Ù„ÙˆØ­Ø© Ø§Ù„Ø£Ø²Ø±Ø§Ø±        JPanel buttonPanel = new JPanel(new FlowLayout(FlowLayout.CENTER, 30, 0));        buttonPanel.setOpaque(false);        buttonPanel.setBorder(BorderFactory.createEmptyBorder(20, 0, 0, 0));        back = createStyledButton("â†©ï¸ BACK TO DASHBOARD", new Color(70, 130, 180));        JButton refreshBtn = createStyledButton("ğŸ”„ REFRESH", new Color(60, 179, 113));        buttonPanel.add(back);        buttonPanel.add(refreshBtn);        contentPanel.add(buttonPanel, BorderLayout.SOUTH);        // Ø¥Ø¶Ø§ÙØ© Ø¬Ù…ÙŠØ¹ Ø§Ù„Ù…ÙƒÙˆÙ†Ø§Øª        mainPanel.add(contentPanel, BorderLayout.CENTER);        add(mainPanel);        // Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„Ù†Ø§ÙØ°Ø©        setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);        // ØªÙˆØ³ÙŠØ· Ø§Ù„Ù†Ø§ÙØ°Ø© ÙÙŠ Ø§Ù„Ø´Ø§Ø´Ø©        setSize(1000, 700);        centerWindow();        setResizable(true);        setVisible(true);        // Ø¥Ø¶Ø§ÙØ© Action Listeners        back.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                dispose();                new C_Reception();            }        });        refreshBtn.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                refreshTable();            }        });    }    private JButton createStyledButton(String text, Color baseColor) {        JButton button = new JButton(text) {            @Override            protected void paintComponent(Graphics g) {                Graphics2D g2d = (Graphics2D) g;                g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);                Color bgColor = getModel().isRollover() ? baseColor.brighter() : baseColor;                GradientPaint gradient = new GradientPaint(                        0, 0, bgColor,                        0, getHeight(), bgColor.darker()                );                g2d.setPaint(gradient);                g2d.fillRoundRect(0, 0, getWidth(), getHeight(), 15, 15);                g2d.setColor(new Color(255, 215, 0));                g2d.setStroke(new BasicStroke(1.5f));                g2d.drawRoundRect(1, 1, getWidth()-2, getHeight()-2, 15, 15);                super.paintComponent(g);            }        };        button.setFont(new Font("Segoe UI", Font.BOLD, 14));        button.setForeground(Color.WHITE);        button.setFocusPainted(false);        button.setBorderPainted(false);        button.setContentAreaFilled(false);        button.setPreferredSize(new Dimension(200, 45));        button.setCursor(new Cursor(Cursor.HAND_CURSOR));        return button;    }    private JPanel createStatCard(String icon, String title, String value, Color color) {        JPanel card = new JPanel() {            @Override            protected void paintComponent(Graphics g) {                super.paintComponent(g);                Graphics2D g2d = (Graphics2D) g;                g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);                g2d.setColor(new Color(color.getRed(), color.getGreen(), color.getBlue(), 40));                g2d.fillRoundRect(0, 0, getWidth(), getHeight(), 15, 15);                g2d.setColor(new Color(color.getRed(), color.getGreen(), color.getBlue(), 120));                g2d.setStroke(new BasicStroke(1.5f));                g2d.drawRoundRect(1, 1, getWidth()-2, getHeight()-2, 15, 15);            }        };        card.setLayout(new BorderLayout());        card.setBorder(BorderFactory.createEmptyBorder(15, 15, 15, 15));        JLabel iconLabel = new JLabel(icon, SwingConstants.CENTER);        iconLabel.setFont(new Font("Segoe UI Emoji", Font.PLAIN, 28));        iconLabel.setForeground(color.brighter());        JLabel titleLabel = new JLabel(title, SwingConstants.CENTER);        titleLabel.setFont(new Font("Segoe UI", Font.BOLD, 14));        titleLabel.setForeground(new Color(240, 240, 240));        JLabel valueLabel = new JLabel(value, SwingConstants.CENTER);        valueLabel.setFont(new Font("Segoe UI", Font.BOLD, 24));        valueLabel.setForeground(color.brighter());        JPanel contentPanel = new JPanel();        contentPanel.setOpaque(false);        contentPanel.setLayout(new BoxLayout(contentPanel, BoxLayout.Y_AXIS));        contentPanel.add(iconLabel);        contentPanel.add(Box.createVerticalStrut(10));        contentPanel.add(titleLabel);        contentPanel.add(Box.createVerticalStrut(5));        contentPanel.add(valueLabel);        card.add(contentPanel, BorderLayout.CENTER);        return card;    }    private String getAvailableCount() {        try {            Z_Con c = new Z_Con();            ResultSet rs = c.statement.executeQuery("SELECT COUNT(*) FROM room WHERE availability='Available'");            if (rs.next()) {                return rs.getString(1);            }        } catch (Exception e) {            e.printStackTrace();        }        return "0";    }    private String getOccupiedCount() {        try {            Z_Con c = new Z_Con();            ResultSet rs = c.statement.executeQuery("SELECT COUNT(*) FROM room WHERE availability='Occupied'");            if (rs.next()) {                return rs.getString(1);            }        } catch (Exception e) {            e.printStackTrace();        }        return "0";    }    private void refreshTable() {        try {            Z_Con c = new Z_Con();            String RoomInfo = "SELECT * FROM room ORDER BY roomnumber";            ResultSet resultSet = c.statement.executeQuery(RoomInfo);            // ØªØ¹ÙŠÙŠÙ† Ø§Ù„Ù†Ù…ÙˆØ°Ø¬ Ø§Ù„Ø¬Ø¯ÙŠØ¯            table.setModel(DbUtils.resultSetToTableModel(resultSet));            // Ø¥Ø¹Ø§Ø¯Ø© ØªØ·Ø¨ÙŠÙ‚ ØªØ®ØµÙŠØµØ§Øª Ø§Ù„Ø£Ø¹Ù…Ø¯Ø©            if (table.getColumnCount() > 0) {                // ØªØ®ØµÙŠØµ Ø±Ø£Ø³ Ø§Ù„Ø¬Ø¯ÙˆÙ„                JTableHeader header = table.getTableHeader();                header.setFont(new Font("Segoe UI", Font.BOLD, 14));                header.setBackground(new Color(30, 60, 90));                header.setForeground(new Color(255, 215, 0));                header.revalidate();                header.repaint();                // ØªØ·Ø¨ÙŠÙ‚ Ø§Ù„ØªØ®ØµÙŠØµ Ø¹Ù„Ù‰ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ø¹Ù…Ø¯Ø©                for (int i = 0; i < table.getColumnCount(); i++) {                    DefaultTableCellRenderer renderer = new DefaultTableCellRenderer();                    renderer.setHorizontalAlignment(JLabel.CENTER);                    renderer.setForeground(new Color(240, 240, 240));                    renderer.setBackground(new Color(255, 255, 255, 20));                    table.getColumnModel().getColumn(i).setCellRenderer(renderer);                }                // Ø¥Ø¹Ø§Ø¯Ø© ØªØ·Ø¨ÙŠÙ‚ ØªØ®ØµÙŠØµ Ø§Ù„Ø­Ø§Ù„Ø©                DefaultTableCellRenderer statusRenderer = new DefaultTableCellRenderer() {                    @Override                    public Component getTableCellRendererComponent(JTable table, Object value,                                                                   boolean isSelected, boolean hasFocus, int row, int column) {                        Component c = super.getTableCellRendererComponent(table, value, isSelected, hasFocus, row, column);                        if (column == 1 && value != null) {                            String status = value.toString();                            if (status.equalsIgnoreCase("Available")) {                                c.setBackground(new Color(60, 179, 113, 100));                                c.setForeground(new Color(200, 255, 200));                            } else if (status.equalsIgnoreCase("Occupied")) {                                c.setBackground(new Color(231, 76, 60, 100));                                c.setForeground(new Color(255, 200, 200));                            } else {                                c.setBackground(new Color(255, 193, 7, 100));                                c.setForeground(new Color(255, 255, 200));                            }                        } else if (column == 2 && value != null) {                            String cleanStatus = value.toString();                            if (cleanStatus.equalsIgnoreCase("Clean")) {                                c.setBackground(new Color(46, 204, 113, 80));                                c.setForeground(new Color(200, 255, 200));                            } else {                                c.setBackground(new Color(255, 193, 7, 80));                                c.setForeground(new Color(255, 255, 200));                            }                        } else {                            c.setBackground(new Color(255, 255, 255, 20));                            c.setForeground(new Color(240, 240, 240));                        }                        if (isSelected) {                            c.setBackground(new Color(70, 130, 180, 150));                            c.setForeground(Color.WHITE);                        }                        return c;                    }                };                statusRenderer.setHorizontalAlignment(JLabel.CENTER);                if (table.getColumnCount() >= 2) {                    table.getColumnModel().getColumn(1).setCellRenderer(statusRenderer);                }                if (table.getColumnCount() >= 3) {                    table.getColumnModel().getColumn(2).setCellRenderer(statusRenderer);                }            }            // Ø¥Ø¹Ø§Ø¯Ø© Ø±Ø³Ù… Ø§Ù„Ø¬Ø¯ÙˆÙ„            table.revalidate();            table.repaint();            // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª            updateStatistics();            // Ø¥Ø¹Ø§Ø¯Ø© Ø±Ø³Ù… ÙƒÙ„ ÙˆØ§Ø¬Ù‡Ø© Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…            SwingUtilities.updateComponentTreeUI(this);            JOptionPane.showMessageDialog(this,                    "Room data refreshed successfully!\n" +                            "Total Rooms: " + table.getRowCount() + "\n" +                            "Available: " + getAvailableCount() + "\n" +                            "Occupied: " + getOccupiedCount(),                    "Refresh Complete",                    JOptionPane.INFORMATION_MESSAGE);        } catch (Exception e) {            e.printStackTrace();            JOptionPane.showMessageDialog(this,                    "Error refreshing data: " + e.getMessage(),                    "Refresh Error",                    JOptionPane.ERROR_MESSAGE);        }    }    // Ø¯Ø§Ù„Ø© Ù„ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª    private void updateStatistics() {        try {            Z_Con c = new Z_Con();            // Ø§Ù„Ø­ØµÙˆÙ„ Ø¹Ù„Ù‰ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø¬Ø¯ÙŠØ¯Ø©            ResultSet totalRs = c.statement.executeQuery("SELECT COUNT(*) FROM room");            ResultSet availableRs = c.statement.executeQuery("SELECT COUNT(*) FROM room WHERE availability='Available'");            ResultSet occupiedRs = c.statement.executeQuery("SELECT COUNT(*) FROM room WHERE availability='Occupied'");            String total = "0";            String available = "0";            String occupied = "0";            if (totalRs.next()) total = totalRs.getString(1);            if (availableRs.next()) available = availableRs.getString(1);            if (occupiedRs.next()) occupied = occupiedRs.getString(1);            // ØªØ­Ø¯ÙŠØ« Ø¨Ø·Ø§Ù‚Ø§Øª Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª            if (statsPanel != null && statsPanel.getComponentCount() == 3) {                updateStatCard(0, "ğŸ›ï¸", "Total Rooms", total);                updateStatCard(1, "âœ…", "Available", available);                updateStatCard(2, "ğŸ”„", "Occupied", occupied);            }        } catch (Exception e) {            e.printStackTrace();        }    }    // Ø¯Ø§Ù„Ø© Ù„ØªØ­Ø¯ÙŠØ« Ø¨Ø·Ø§Ù‚Ø© Ø¥Ø­ØµØ§Ø¦ÙŠØ© Ù…Ø­Ø¯Ø¯Ø©    private void updateStatCard(int index, String icon, String title, String value) {        if (statsPanel != null && statsPanel.getComponentCount() > index) {            if (statsPanel.getComponent(index) instanceof JPanel) {                JPanel card = (JPanel) statsPanel.getComponent(index);                // Ù…Ø³Ø­ Ø§Ù„Ù…ÙƒÙˆÙ†Ø§Øª Ø§Ù„Ù‚Ø¯ÙŠÙ…Ø©                card.removeAll();                // Ø¥Ø¶Ø§ÙØ© Ø§Ù„Ù…ÙƒÙˆÙ†Ø§Øª Ø§Ù„Ø¬Ø¯ÙŠØ¯Ø©                card.setLayout(new BorderLayout());                JPanel contentPanel = new JPanel();                contentPanel.setOpaque(false);                contentPanel.setLayout(new BoxLayout(contentPanel, BoxLayout.Y_AXIS));                Color[] statsColors = {                        new Color(70, 130, 180),   // Steel Blue                        new Color(60, 179, 113),   // Medium Sea Green                        new Color(231, 76, 60)     // Red                };                JLabel iconLabel = new JLabel(icon, SwingConstants.CENTER);                iconLabel.setFont(new Font("Segoe UI Emoji", Font.PLAIN, 28));                iconLabel.setForeground(statsColors[index].brighter());                JLabel titleLabel = new JLabel(title, SwingConstants.CENTER);                titleLabel.setFont(new Font("Segoe UI", Font.BOLD, 14));                titleLabel.setForeground(new Color(240, 240, 240));                JLabel valueLabel = new JLabel(value, SwingConstants.CENTER);                valueLabel.setFont(new Font("Segoe UI", Font.BOLD, 24));                valueLabel.setForeground(statsColors[index].brighter());                contentPanel.add(iconLabel);                contentPanel.add(Box.createVerticalStrut(10));                contentPanel.add(titleLabel);                contentPanel.add(Box.createVerticalStrut(5));                contentPanel.add(valueLabel);                card.add(contentPanel, BorderLayout.CENTER);                // Ø¥Ø¹Ø§Ø¯Ø© Ø±Ø³Ù… Ø§Ù„Ø¨Ø·Ø§Ù‚Ø©                card.revalidate();                card.repaint();            }        }    }    private void centerWindow() {        Dimension screenSize = Toolkit.getDefaultToolkit().getScreenSize();        int x = (screenSize.width - getWidth()) / 2;        int y = (screenSize.height - getHeight()) / 2;        setLocation(x, y);    }    public static void main(String[] args) {        // Ø¥Ø¹Ø¯Ø§Ø¯ Look and Feel        try {            UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());        } catch (Exception e) {            e.printStackTrace();        }        SwingUtilities.invokeLater(() -> {            new F_Room();        });    }}